import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import WelcomeScreen from './components/WelcomeScreen';
import './styles/App.css';
import './styles/main.css';
import './styles/skyline.css';

function App() {
  const quotes = [
    "The sky is the daily bread of the eyes.",
    "Weather is a great metaphor for life.",
    "Sunshine is the best medicine.",
  ];

  return (
    <div className="app skyline-theme">
      <Router>
        <Routes>
          <Route path="/" element={<WelcomeScreen quotes={quotes} />} />
          {/* Placeholder for weather route; add your Weather component if exists */}
          <Route path="/weather" element={<div>Weather Page Placeholder</div>} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
