import React from 'react';

const WeatherDisplay = ({ weather }) => {
  if (!weather) return null;

  return (
    <div className="p-6 bg-card-bg backdrop-blur-lg rounded-xl shadow-neumorphic text-center animate-slideIn relative overflow-hidden group">
      <div className="absolute inset-0 sprinkle-stars opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
      <h2 className="text-3xl font-bold text-heading">
        {weather.name}, {weather.sys.country}
      </h2>
      <img
        src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
        alt="Weather Icon"
        className="mx-auto animate-pulse"
      />
      <p className="text-2xl text-primary">{Math.round(weather.main.temp)}Â°C</p>
      <p className="capitalize text-primary">{weather.weather[0].description}</p>
      <p className="text-primary">Humidity: {weather.main.humidity}%</p>
      <p className="text-primary">Wind Speed: {weather.wind.speed} m/s</p>
    </div>
  );
};

export default WeatherDisplay;
