import React from 'react';
import WeatherIcon from './WeatherIcon';

const DailyForecast = ({ dailyData }) => {
  if (!dailyData || dailyData.length === 0) return null;

  const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <div className="daily-forecast glass">
      <h3>5-Day Forecast</h3>
      <div className="daily-list">
        {dailyData.slice(1, 6).map((day, index) => (
          <div key={index} className="daily-item">
            <p>{days[new Date(day.dt * 1000).getDay()]}</p>
            <div className="weather-icon">
              <WeatherIcon condition={day.weather[0].main} size={30} />
            </div>
            <div className="temp-range">
              <span className="max-temp">{Math.round(day.temp.max)}°</span>
              <span className="min-temp">{Math.round(day.temp.min)}°</span>
            </div>
            <p className="weather-desc">{day.weather[0].main}</p>
            <div className="additional-info">
              <span>Humidity: {day.humidity}%</span>
              <span>Wind: {Math.round(day.wind_speed)} km/h</span>
              <span>Rain: {day.pop ? Math.round(day.pop * 100) : 0}%</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DailyForecast;
