import React from 'react';

const WeatherDisplay = ({ weather }) => {
  if (!weather) return null;

  const weatherCondition = weather.weather[0].main;
  const weatherImages = {
    Clear: 'url(https://images.unsplash.com/photo-1622279457486-62f8e8f31e49?q=80&w=2070&auto=format&fit=crop)',
    Clouds: 'url(https://images.unsplash.com/photo-1531147646552-7b33be1e4903?q=80&w=2070&auto=format&fit=crop)',
    Rain: 'url(https://images.unsplash.com/photo-1515694346937-94d85e41e6f0?q=80&w=2070&auto=format&fit=crop)',
    Drizzle: 'url(https://images.unsplash.com/photo-1515694346937-94d85e41e6f0?q=80&w=2070&auto=format&fit=crop)',
    Thunderstorm: 'url(https://images.unsplash.com/photo-1605727216801-ecdff9bd81c1?q=80&w=2070&auto=format&fit=crop)',
    Snow: 'url(https://images.unsplash.com/photo-1542601098-3adb3baeb1ec?q=80&w=2070&auto=format&fit=crop)',
    Mist: 'url(https://images.unsplash.com/photo-1541912766668-53d714c7b80a?q=80&w=2070&auto=format&fit=crop)',
    Fog: 'url(https://images.unsplash.com/photo-1541912766668-53d714c7b80a?q=80&w=2070&auto=format&fit=crop)',
    Haze: 'url(https://images.unsplash.com/photo-1541912766668-53d714c7b80a?q=80&w=2070&auto=format&fit=crop)',
  };

  const backgroundStyle = {
    backgroundImage: weatherImages[weatherCondition] || 'linear-gradient(to bottom, #6b7280, #374151)',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  };

  return (
    <div
      className="p-4 sm:p-6 bg-card-bg backdrop-blur-lg rounded-xl shadow-neumorphic text-center animate-slideIn relative overflow-hidden group"
      style={backgroundStyle}
    >
      <div className="absolute inset-0 sprinkle-stars opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
      <h2 className="text-xl sm:text-3xl font-bold text-heading">
        {weather.name}, {weather.sys.country}
      </h2>
      <img
        src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
        alt="Weather Icon"
        className="mx-auto animate-pulse w-16 sm:w-24"
      />
      <p className="text-lg sm:text-2xl text-primary">{Math.round(weather.main.temp)}Â°C</p>
      <p className="capitalize text-primary text-sm sm:text-base">{weather.weather[0].description}</p>
      <p className="text-primary text-sm sm:text-base">Humidity: {weather.main.humidity}%</p>
      <p className="text-primary text-sm sm:text-base">Wind Speed: {weather.wind.speed} m/s</p>
    </div>
  );
};

export default WeatherDisplay;
