const WeatherDisplay = ({ weather }) => {
    if (!weather) return null;
  
    return (
      <div className="p-6 bg-white bg-opacity-20 backdrop-blur-lg rounded-xl shadow-neumorphic text-center animate-slideIn relative overflow-hidden group">
        <div className="absolute inset-0 sprinkle-stars opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
        <h2 className="text-3xl font-bold bg-gradient-to-r from-yellow-400 to-red-400 bg-clip-text text-transparent">
          {weather.name}, {weather.sys.country}
        </h2>
        <img
          src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
          alt="Weather Icon"
          className="mx-auto animate-pulse"
        />
        <p className="text-2xl">{Math.round(weather.main.temp)}Â°C</p>
        <p className="capitalize">{weather.weather[0].description}</p>
        <p>Humidity: {weather.main.humidity}%</p>
        <p>Wind Speed: {weather.wind.speed} m/s</p>
      </div>
    );
  };
  
  export default WeatherDisplay;
