import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import CurrentWeather from './components/CurrentWeather';
import HourlyForecast from './components/HourlyForecast';
import DailyForecast from './components/DailyForecast';
import WelcomeScreen from './components/WelcomeScreen';
import './styles/main.css';

const API_KEY = 'e597c1f8e3bdd6e8f058742f2b2c9caf';

const App = () => {
  const [showWelcome, setShowWelcome] = useState(true);
  const [weatherData, setWeatherData] = useState(null);
  const [hourlyData, setHourlyData] = useState([]);
  const [dailyData, setDailyData] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchWeatherData = async (city) => {
    if (!city.trim()) return;

    setLoading(true);
    try {
      const currentResponse = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );

      setWeatherData(currentResponse.data);
      setShowWelcome(false);

      const forecastResponse = await axios.get(
        `https://api.openweathermap.org/data/2.5/onecall?lat=${currentResponse.data.coord.lat}&lon=${currentResponse.data.coord.lon}&exclude=minutely,alerts&appid=${API_KEY}&units=metric`
      );

      setHourlyData(forecastResponse.data.hourly);
      setDailyData(forecastResponse.data.daily);
    } catch (err) {
      console.error("Error fetching weather data:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      {showWelcome ? (
        <WelcomeScreen onGetStarted={() => setShowWelcome(false)} />
      ) : (
        <div className="weather-container">
          <SearchBar onSearch={fetchWeatherData} />

          {loading ? (
            <div className="loading">
              <div className="loader"></div>
            </div>
          ) : weatherData ? (
            <>
              <CurrentWeather data={{ ...weatherData, pop: hourlyData[0]?.pop }} />
              <HourlyForecast hourlyData={hourlyData} />
              <DailyForecast dailyData={dailyData} />
            </>
          ) : (
            <div className="empty-state">
              <h3>Search for a city to see weather information</h3>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
