import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/skyline.css';

const WelcomeScreen = ({ quotes }) => {
  const navigate = useNavigate();
  const [currentQuote, setCurrentQuote] = useState('');

  useEffect(() => {
    setCurrentQuote(quotes[Math.floor(Math.random() * quotes.length)]);
    const interval = setInterval(() => {
      setCurrentQuote(quotes[Math.floor(Math.random() * quotes.length)]);
    }, 8000);
    return () => clearInterval(interval);
  }, [quotes]);

  return (
    <div className="welcome-screen glass skyline-theme">
      <h1>Weather Forecast</h1>
      <div className="quote-container">
        <p className="highlight-text">"{currentQuote}"</p>
      </div>
      <div className="button-group">
        <button onClick={() => navigate('/weather')} className="glow-button skyline-theme">
          Get Started
        </button>
      </div>
    </div>
  );
};

export default WelcomeScreen;
