import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import WelcomeScreen from './components/WelcomeScreen';
import SearchBar from './components/SearchBar';
import CurrentWeather from './components/CurrentWeather';
import HourlyForecast from './components/HourlyForecast';
import DailyForecast from './components/DailyForecast';
import './styles/main.css';
import './styles/skyline.css';

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [hourlyData, setHourlyData] = useState([]);
  const [dailyData, setDailyData] = useState([]);
  const [error, setError] = useState(null);
  const API_KEY = '8129fc7973f8193956223b8b4e5d46a0'; // Updated with your new API key
  const quotes = [
    "The sky is the daily bread of the eyes.",
    "Weather is a great metaphor for life.",
    "Sunshine is the best medicine.",
  ];

  const fetchWeatherData = async (city = 'London') => {
    setError(null); // Reset error before new fetch
    try {
      // Test current weather API
      const currentResponse = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${API_KEY}`,
        { timeout: 5000 } // 5-second timeout
      );
      const lat = currentResponse.data.coord.lat;
      const lon = currentResponse.data.coord.lon;

      // Test OneCall API
      const forecastResponse = await axios.get(
        `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=minutely,alerts&units=metric&appid=${API_KEY}`,
        { timeout: 5000 }
      );

      setWeatherData({
        ...currentResponse.data,
        main: currentResponse.data.main,
        weather: currentResponse.data.weather,
        name: currentResponse.data.name,
      });
      setHourlyData(forecastResponse.data.hourly.map(hour => ({
        dt: hour.dt,
        temp: hour.temp,
        weather: hour.weather,
        pop: hour.pop,
      })));
      setDailyData(forecastResponse.data.daily.map(day => ({
        dt: day.dt,
        temp: { max: day.temp.max, min: day.temp.min },
        weather: day.weather,
        humidity: day.humidity,
        wind_speed: day.wind_speed,
        pop: day.pop,
      })));
      console.log('Success:', { current: currentResponse.data, forecast: forecastResponse.data });
    } catch (err) {
      const errorMsg = err.response?.status === 401
        ? 'Invalid API key. Please get a new one from OpenWeatherMap.'
        : err.response?.status === 429
        ? 'Rate limit exceeded. Wait and try again.'
        : err.code === 'ECONNABORTED'
        ? 'Request timed out. Check your internet.'
        : 'Failed to fetch weather data. Check API key or internet.';
      console.error('Fetch error:', err.message, err.response?.data || err);
      setError(errorMsg);
    }
  };

  useEffect(() => {
    fetchWeatherData();
  }, []);

  return (
    <div className="app skyline-theme">
      <Router>
        <Routes>
          <Route path="/" element={<WelcomeScreen quotes={quotes} />} />
          <Route
            path="/weather"
            element={
              <div className="weather-container">
                <SearchBar onSearch={fetchWeatherData} />
                {error ? (
                  <div className="error">
                    {error} <br />
                    <button onClick={() => fetchWeatherData()}>Retry</button>
                  </div>
                ) : weatherData ? (
                  <>
                    <CurrentWeather data={weatherData} />
                    <HourlyForecast hourlyData={hourlyData} />
                    <DailyForecast dailyData={dailyData} />
                  </>
                ) : (
                  <div className="loading">
                    <div className="loader"></div>
                    <p>Loading...</p>
                  </div>
                )}
              </div>
            }
          />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
