const { useState } = React;

const SearchBar = ({ setWeather, setForecast, setError }) => {
  const [city, setCity] = useState("");
  const API_KEY = "a164e83e9dd0c5a2e2eec713798b5175";

  const fetchWeather = async () => {
    try {
      setError("");
      const weatherRes = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeather(weatherRes.data);
      const forecastRes = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}&units=metric`
      );
      setForecast(forecastRes.data);
    } catch (err) {
      setError("City not found or API error!");
      setWeather(null);
      setForecast(null);
    }
  };

  const handleSearch = () => {
    if (city.trim()) fetchWeather();
  };

  return (
    <div className="flex justify-center mb-8">
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city name"
        className="p-3 rounded-l-xl bg-white bg-opacity-20 backdrop-blur-lg text-white placeholder-gray-300 focus:outline-none shadow-neumorphic transition-all duration-300 hover:scale-105"
      />
      <button
        onClick={handleSearch}
        className="p-3 bg-gradient-to-r from-red-500 via-yellow-500 to-blue-500 text-white rounded-r-xl hover:scale-110 transition-transform duration-300 shadow-neumorphic animate-pulse-slow"
      >
        Search
      </button>
    </div>
  );
};
