import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import WelcomeScreen from './components/WelcomeScreen';
import WeatherDashboard from './components/WeatherDashboard';
import './styles/main.css';
import './styles/glassmorphism.css';

const API_KEY = 'e597c1f8e3bdd6e8f058742f2b2c9caf';

const weatherQuotes = [
  "Sunshine is delicious, rain is refreshing, wind braces us up, snow is exhilarating.",
  "There is no such thing as bad weather, only different kinds of good weather.",
  "Weather is a great metaphor for life — sometimes it's good, sometimes it's bad.",
  "To appreciate the beauty of a snowflake it is necessary to stand out in the cold.",
  "Nature is so powerful, so strong. Capturing its essence is not easy."
];

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<WelcomeScreen quotes={weatherQuotes} />} />
        <Route path="/weather" element={<WeatherDashboard apiKey={API_KEY} />} />
      </Routes>
    </Router>
  );
};

export default App;
