import React from 'react';
import { WiDaySunny, WiCloudy, WiRain, WiSnow, WiThunderstorm, WiFog } from 'react-icons/wi';

const WeatherIcon = ({ condition, size = 24 }) => {
  const iconMap = {
    'clear': <WiDaySunny size={size} />,
    'clouds': <WiCloudy size={size} />,
    'rain': <WiRain size={size} />,
    'snow': <WiSnow size={size} />,
    'thunderstorm': <WiThunderstorm size={size} />,
    'mist': <WiFog size={size} />,
    'fog': <WiFog size={size} />,
    'default': <WiDaySunny size={size} />
  };

  const conditionKey = condition?.toLowerCase() || 'default';
  return iconMap[conditionKey] || iconMap['default'];
};

export default WeatherIcon;
