const { useState, useEffect } = React;

const QuoteDisplay = () => {
  const quotes = [
    "Ride the storm, chase the rainbow!",
    "Every cloud hides a silver lining.",
    "Weather changes, dreams don't.",
    "Let the winds guide your path.",
    "Sunshine follows the rain."
  ];
  const [quote, setQuote] = useState("");

  useEffect(() => {
    setQuote(quotes[Math.floor(Math.random() * quotes.length)]);
  }, []);

  return (
    <div className="text-center mb-8">
      <p className="text-xl italic bg-gradient-to-r from-purple-400 via-pink-400 to-yellow-400 bg-clip-text text-transparent animate-fadeIn">
        "{quote}"
      </p>
    </div>
  );
};
