const DailyForecast = ({ forecast }) => {
    if (!forecast) return null;
  
    const daily = forecast.list.filter((item, idx) => idx % 8 === 0).slice(0, 5);
  
    return (
      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 bg-gradient-to-r from-pink-400 to-purple-400 bg-clip-text text-transparent animate-fadeIn">
          5-Day Forecast
        </h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
          {daily.map((item, idx) => (
            <div
              key={idx}
              className="p-4 bg-white bg-opacity-20 backdrop-blur-lg rounded-lg text-center shadow-neumorphic animate-slideIn hover:border-2 hover:border-gradient-rainbow"
            >
              <p>{new Date(item.dt * 1000).toLocaleDateString()}</p>
              <img
                src={`http://openweathermap.org/img/wn/${item.weather[0].icon}.png`}
                alt="Weather Icon"
                className="mx-auto"
              />
              <p>{Math.round(item.main.temp)}Â°C</p>
              <p className="capitalize">{item.weather[0].description}</p>
            </div>
          ))}
        </div>
      </div>
    );
  };
