import { useState } from 'react';
import QuoteDisplay from './QuoteDisplay';
import SearchBar from './SearchBar';
import WeatherDisplay from './WeatherDisplay';
import HourlyForecast from './HourlyForecast';
import DailyForecast from './DailyForecast';
import './styles.css';

const App = () => {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState("");

  return (
    <div className="max-w-5xl w-full p-8 bg-white bg-opacity-10 backdrop-blur-lg rounded-2xl shadow-2xl animate-fadeIn">
      <QuoteDisplay />
      <SearchBar setWeather={setWeather} setForecast={setForecast} setError={setError} />
      {error && <p className="text-red-400 text-center animate-pulse">{error}</p>}
      <WeatherDisplay weather={weather} />
      <HourlyForecast forecast={forecast} />
      <DailyForecast forecast={forecast} />
    </div>
  );
};

export default App;
