const HourlyForecast = ({ forecast }) => {
    if (!forecast) return null;
  
    const hourly = forecast.list.slice(0, 8);
  
    return (
      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 bg-gradient-to-r from-blue-400 to-green-400 bg-clip-text text-transparent animate-fadeIn">
          Hourly Forecast
        </h3>
        <div className="flex overflow-x-auto space-x-4 pb-4 snap-x snap-mandatory">
          {hourly.map((item, idx) => (
            <div
              key={idx}
              className="p-4 min-w-[150px] bg-white bg-opacity-20 backdrop-blur-lg rounded-lg text-center shadow-neumorphic animate-slideIn hover:border-2 hover:border-gradient-rainbow snap-center"
            >
              <p>{new Date(item.dt * 1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
              <img
                src={`http://openweathermap.org/img/wn/${item.weather[0].icon}.png`}
                alt="Weather Icon"
                className="mx-auto"
              />
              <p>{Math.round(item.main.temp)}Â°C</p>
              <p className="capitalize">{item.weather[0].description}</p>
            </div>
          ))}
        </div>
      </div>
    );
  };
  
  export default HourlyForecast;
